/**
 * @param {Node} node
 * @param {string} name
 * @param {Handler} callback
 */
export function zone(node: Node, name: string, callback: Handler): void
export type Node = import('mdast').Root | import('mdast').Content
export type Parent = Extract<Node, import('mdast').Parent>
export type ZoneInfo = {
  start: number
  end: number
  parent: Parent | null
}
export type Handler = (
  start: Node,
  between: Array<Node>,
  end: Node,
  info: ZoneInfo
) => Array<Node> | null | undefined | void
